import { useState } from "react";

const AddStats = ({
  onRecord,
  setIsSubmitted,
  revertStat,
  setIsReverted,
  deleteStat,
  setIsDeleted,
}) => {
  const [text, setText] = useState("");
  const [type, setType] = useState("");
  const [prevText, setPrevText] = useState("");
  const [prevType, setPrevType] = useState("");

  const onSubmit = (e) => {
    e.preventDefault();

    if (!text) {
      alert("Please add a stat");
      return;
    }

    onRecord({ text, type });
    setText("");
    setType("");
    setIsSubmitted(true);
    setPrevText(text);
    setPrevType(type);
  };

  const onRevert = (e) => {
    e.preventDefault();
    if (!prevText) {
      alert("No previous stat");
      return;
    }
    revertStat({ prevText, prevType });
    setIsReverted(true);
    setPrevText("");
    setPrevType("");
  };

  const onDelete = (e) => {
    e.preventDefault();
    if (!text) {
      alert("No stat to delete");
      return;
    }
    deleteStat({ text, type });
    setIsDeleted(true);
    setText("");
    setType("");
  };

  const handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      onSubmit(e);
    }
  };

  const twoCalls = (e) => {
    setText(e.target.value);
    setIsSubmitted(false);
    setIsReverted(false);
    setIsDeleted(false);
  };

  return (
    <form className="add-form" onSubmit={onSubmit} onKeyDown={handleKeyPress}>
      <div className="form-control">
        <label>Player Name/Initials/Number</label>
        <input
          id="text"
          type="text"
          placeholder="Name"
          value={text}
          onChange={twoCalls}
          autoComplete="off"
        />
      </div>
      <div>
        <label>Stat Type</label>
      </div>
      <div className="form-control stat">
        <div className="stat-cell">
          <input
            id="or"
            className="stat-btn"
            type="button"
            value="OR"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="dr"
            className="stat-btn"
            type="button"
            value="DR"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="ast"
            className="stat-btn"
            type="button"
            value="AST"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="to"
            className="stat-btn"
            type="button"
            value="TO"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="fifty"
            className="stat-btn"
            type="button"
            value="50/50"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="defl"
            className="stat-btn"
            type="button"
            value="Defl"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="stl"
            className="stat-btn"
            type="button"
            value="STL"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="blk"
            className="stat-btn"
            type="button"
            value="BLK"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="madeTwo"
            className="stat-btn"
            type="button"
            value="Made 2"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="madeThree"
            className="stat-btn"
            type="button"
            value="Made 3"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="madeFT"
            className="stat-btn"
            type="button"
            value="Made FT"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="pf"
            className="stat-btn"
            type="button"
            value="PF"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="attemptedTwo"
            className="stat-btn"
            type="button"
            value="Attempt 2"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="attemptedThree"
            className="stat-btn"
            type="button"
            value="Attempt 3"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="attemptedFT"
            className="stat-btn"
            type="button"
            value="Attempt FT"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
        <div className="stat-cell">
          <input
            id="df"
            className="stat-btn"
            type="button"
            value="DF"
            onClick={(e) => setType(e.target.id)}
          />
        </div>
      </div>
      <div className="func-btns">
        <input
          id="submit"
          type="submit"
          value="record"
          className="btn  btn-block"
        />
        <input
          id="revert"
          type="button"
          value="revert"
          className="btn  btn-block"
          onClick={onRevert}
        />
        <input
          id="delete"
          type="button"
          value="delete"
          className="btn  btn-block"
          onClick={onDelete}
        />
      </div>
    </form>
  );
};

export default AddStats;
