import { useState, useEffect } from "react";
import Axios from "axios";

function Check() {
  const [listOfStats, setListOfStats] = useState([]);
  const [date, setDate] = useState("");
  const [stats, setStats] = useState([]);

  useEffect(() => {
    Axios.get("http://localhost:3001/getStats").then((response) => {
      setListOfStats(response.data);
    });
  }, []);

  const createStat = () => {
    Axios.post("http://localhost:3001/createStats", {
      date,
      stats,
    }).then((response) => {
      setListOfStats([
        ...listOfStats,
        {
          date,
          stats,
        },
      ]);
      alert("stat created");
    });
  };

  return (
    <div className="App">
      <div className="statsDisplay">
        {listOfStats.map((stat) => {
          return (
            <div>
              <h1>Date: {stat.date}</h1>
              <h1>Stats: {stat.stats}</h1>
            </div>
          );
        })}
      </div>
      <div>
        <input type="date" placeholder="Date..." onChange={(event) => {
            setDate(event.target.value);
          }}/>
        <input type="text" placeholder="Stat..." onChange={(event) => {
            setStats(event.target.value);
          }}/>
        <button onClick={createStat}>create stat</button>
      </div>
    </div>
  );
}

export default Check;
